<script>
export default {
  name: 'Tool',
  computed: {
    permanentImages () {
      return this.$store.getters.getPermanentImages
    },
    levelControl () {
      return this.$store.getters.getLevelControl
    }
  },
  methods: {
    computeFontSize (text) {
      if (text.length === 1) return '3rem'
      else if (text.length <= 3) return '2rem'
      else if (text.length === 4) return '1.5rem'
      else if (text.length === 5) return '1.1rem'
      else return '1rem'
    },
    computeName (text) {
      if (text === 'white') return 'always'
      else return text
    }
  },
  props: ['denomination', 'hideDenom']
}
</script>

